import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../Components/Navbar';
import Modal from '../routes/Modal';
import "./Contactstyles.css";
import { useTranslation } from 'react-i18next';

function Contact() {
  const { t } = useTranslation();
  const [hospitals, setHospitals] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    familyname: '',
    email: '',
    phone: '',
    hospital: '',
    message: ''
  });
  const [successMessage, setSuccessMessage] = useState('');
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    axios.get('/api/hospitals')
      .then(response => {
        setHospitals(response.data);
      })
      .catch(error => {
        console.error(t('contact.errorFetchingHospitals'), error);
      });
  }, [t]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('/api/feedback/', formData)
      .then(response => {
        setSuccessMessage(t('contact.successMessage'));
        setFormData({
          name: '',
          familyname: '',
          email: '',
          phone: '',
          hospital: '',
          message: ''
        });
        setShowModal(true);
      })
      .catch(error => {
        console.error(t('contact.errorSubmittingFeedback'), error);
      });
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <Navbar />
      <div className='contact'>
        <div className='healthcontact'>
          <h1>{t('contact.title')}</h1>
          <p>{t('contact.description')}</p>
        </div>

        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">{t('contact.name')}:</label>
            <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div>
            <label htmlFor="familyname">{t('contact.familyname')}:</label>
            <input type="text" id="familyname" name="familyname" value={formData.familyname} onChange={handleChange} required />
          </div>
          <div>
            <label htmlFor="email">{t('contact.email')}:</label>
            <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div>
            <label htmlFor="phone">{t('contact.phone')}:</label>
            <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleChange} />
          </div>
          <div>
            <label htmlFor="hospital">{t('contact.hospital')}:</label>
            <select id="hospital" name="hospital" value={formData.hospital} onChange={handleChange} required>
              <option value="">{t('contact.selectHospital')}</option>
              {hospitals.map((hospital) => (
                <option key={hospital.id} value={hospital.id}>{hospital.name}</option>
              ))}
            </select>
          </div>
          <div>
            <label htmlFor="message">{t('contact.message')}:</label>
            <textarea id="message" name="message" value={formData.message} onChange={handleChange} rows="5" />
          </div>
          <div className='submit-container'>
            <button type="submit">{t('contact.submit')}</button>
          </div>
        </form>
        <div className='footer'>
          <div className='top'>
            <div>
              <h1>{t('footer.title')}</h1>
              <p>{t('footer.description')}</p>
            </div>
          </div>
          <div className='bottom'>
            <div>
              <h2>{t('footer.services')}</h2>
              <a href='/'>{t('footer.conditions')}</a>
              <a href='/'>{t('footer.listings')}</a>
              <a href='/'>{t('footer.whatWeOffer')}</a>
              <a href='/'>{t('footer.howItWorks')}</a>
              <a href='/'>{t('footer.latestNews')}</a>
            </div>
            <div>
              <h2>{t('footer.usefulLinks')}</h2>
              <a href='http://localhost:3000/About'>{t('footer.about')}</a>
              <a href='http://localhost:3000/service'>{t('footer.services')}</a>
              <a href='http://localhost:3000/Hospital'>{t('footer.hospitals')}</a>
              <a href='http://localhost:3000/Contact'>{t('footer.contact')}</a>
            </div>
            <div>
              <h2>{t('footer.followUs')}</h2>
              <a href='/'>{t('footer.facebook')}</a>
              <a href='/'>{t('footer.telegram')}</a>
              <a href='/'>{t('footer.whatsapp')}</a>
              <a href='/'>{t('footer.twitter')}</a>
              <a href='/'>{t('footer.instagram')}</a>
            </div>
            <div>
              <h2>{t('footer.contactUs')}</h2>
              <a href='/'>{t('footer.email')}</a>
              <a href='/'>{t('footer.phone')}</a>
            </div>
          </div>
        </div>
      </div>
      {showModal && <Modal message={successMessage} onClose={handleCloseModal} />}
    </>
  );
}

export default Contact;
