import React, { useState } from 'react';
import "./BookStyles.css";
import Navbar from '../Components/Navbar';
import { useLanguage } from "../language/context"; // Import the language context

function Book() {
  const { translate } = useLanguage(); // Use the translate function from the context
  const [selectedHospital, setSelectedHospital] = useState('');
  const [selectedService, setSelectedService] = useState('');
  const [name, setName] = useState('');
  const [lastname, setLastname] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, such as sending data to an API
    console.log('Form submitted:', { selectedHospital, selectedService, name, lastname, appointmentDate });
    // Reset form fields
    setSelectedHospital('');
    setSelectedService('');
    setName('');
    setLastname('');
    setAppointmentDate('');
  };

  const handleHospitalChange = (e) => {
    setSelectedHospital(e.target.value);
  };

  const handleServiceChange = (e) => {
    setSelectedService(e.target.value);
  };

  return (
    <>
      <Navbar />
      
      <div className='Book'>
        <div className="booking-form">
          <h1>{translate('Booking Appointment')}</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="hospital">{translate('Hospital')}:</label>
              <select
                id="hospital"
                value={selectedHospital}
                onChange={handleHospitalChange}
                required
              >
                <option value="">{translate('Select a hospital')}</option>
                <option value="hospital1">{translate("Menelik Hospital")}</option>
                <option value="hospital2">{translate("Yekatit Hospital")}</option>
                <option value="hospital3">{translate("St.Paul's Hospital")}</option>
                {/* Add more options for other hospitals */}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="service">{translate('Services')}:</label>
              <select
                id="service"
                value={selectedService}
                onChange={handleServiceChange}
                required
              >
                <option value="">{translate('Select a Service')}</option>
                <option value="Service1">{translate("Dental")}</option>
                <option value="Service2">{translate("Maternity")}</option>
                <option value="Service3">{translate("Gynecology")}</option>
                {/* Add more options for other services */}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="name">{translate('First Name')}:</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="lastname">{translate('Last Name')}:</label>
              <input
                type="text"
                id="lastname"
                value={lastname}
                onChange={(e) => setLastname(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="appointment-date">{translate('Appointment Date')}:</label>
              <input
                type="date"
                id="appointment-date"
                value={appointmentDate}
                onChange={(e) => setAppointmentDate(e.target.value)}
                required
              />
            </div>
            <button type="submit">{translate('Book Appointment')}</button>
          </form>
        </div>
        <div className='footer'>
          <div className='top'>
            <div>
              <h1>{translate('Hospital Information')}</h1>
              <p>{translate('Get the information and location of the Hospitals')}.</p>
            </div>
          </div>
          <div className='bottom'>
            <div>
              <h2>{translate('Services')}</h2>
              <a href='/'>{translate('Conditions')}</a>
              <a href='/'>{translate('Listings')}</a>
              <a href='/'>{translate('What we offer')}</a>
              <a href='/'>{translate('How it Works')}</a>
              <a href='/'>{translate('Latest News')}</a>
            </div>
            <div>
              <h2>{translate('Useful Links')}</h2>
              <a href='http://localhost:3000/About'>{translate('About')}</a>
              <a href='http://localhost:3000/service'>{translate('Services')}</a>
              <a href='http://localhost:3000/Hospital'>{translate('Hospitals')}</a>
              <a href='http://localhost:3000/Contact'>{translate('Contact')}</a>
            </div>
            <div>
              <h2>{translate('Follow us')}</h2>
              <a href='/'>Facebook</a>
              <a href='/'>Telegram</a>
              <a href='/'>WhatsApp</a>
              <a href='/'>Twitter</a>
              <a href='/'>Instagram</a>
            </div>
            <div>
              <h2>{translate('Contact Us')}</h2>
              <a href='/'>firii9@gmail.com</a>
              <a href='/'>+251978796545</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Book;
