import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next';

const NearbyHospitals = () => {
  const { t } = useTranslation();
  const [hospitals, setHospitals] = useState([]);
  const [latitude, setLatitude] = useState(null);
  const [longitude, setLongitude] = useState(null);

  // Get user's current location
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setLatitude(position.coords.latitude);
        setLongitude(position.coords.longitude);
      },
      (error) => {
        console.error(t('nearbyHospitals.locationError'), error);
      }
    );
  }, [t]);

  // Fetch hospitals based on user's location
  useEffect(() => {
    const fetchHospitals = async () => {
      if (latitude !== null && longitude !== null) {
        try {
          const response = await axios.get('/api/nearbyhospitals', {
            params: { latitude, longitude },
          });
          setHospitals(response.data.hospitals);
        } catch (error) {
          console.error(t('nearbyHospitals.fetchError'), error);
        }
      }
    };

    fetchHospitals();
  }, [latitude, longitude, t]);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">{t('nearbyHospitals.title')}</h1>
      {hospitals.length > 0 ? (
        <ul className="space-y-4">
          {hospitals.map((hospital, index) => (
            <li key={index} className="p-4 border border-gray-200 rounded-lg shadow-sm">
              <h2 className="text-xl font-semibold">{hospital.name}</h2>
              <p className="text-gray-600">{hospital.location}</p>
              <p className="text-gray-800">{t('nearbyHospitals.distance')}: {hospital.distance.toFixed(2)} km</p>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-500">{t('nearbyHospitals.loading')}</p>
      )}
    </div>
  );
};

export default NearbyHospitals;
