import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const BookingForm = () => {
    const [hospitals, setHospitals] = useState([]);
    const [services, setServices] = useState([]);
    const [selectedHospital, setSelectedHospital] = useState('');
    const [selectedService, setSelectedService] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [appointmentDate, setAppointmentDate] = useState(new Date());
    const [appointmentTime, setAppointmentTime] = useState(new Date());
    const [email, setEmail] = useState('');

    useEffect(() => {
        axios.get('http://localhost:8000/api/hospitals/')
            .then(response => {
                setHospitals(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the hospitals!', error);
            });
    }, []);

    useEffect(() => {
        if (selectedHospital) {
            axios.get(`http://127.0.0.1:8000/api/hospitals/${selectedHospital}/`)
                .then(response => {
                    setServices(response.data);
                })
                .catch(error => {
                    console.error('There was an error fetching the services!', error);
                });
        }
    }, []);
    console.log(selectedService)

    const handleSubmit = (e) => {
        e.preventDefault();

        const hospitalId = parseInt(selectedHospital, 10);
        const serviceId = parseInt(selectedService, 10);
        // Ensure all required fields are filled
        if (!firstName || !lastName || !email || !selectedHospital || !selectedService) {
            alert('Please fill in all the required fields.');
            return;
        }

        const booking = {
            hospital: hospitalId, // Use the validated hospital ID
            service: serviceId, // Use the validated service ID
            first_name: firstName,
            last_name: lastName,
            appointment_date: appointmentDate.toISOString().split('T')[0],
            appointment_time: appointmentTime.toTimeString().split(' ')[0].substring(0, 8), // Ensure HH:MM:SS format
            email: email,
        };

        axios.post('http://localhost:8000/api/appointments/', booking)
            .then(response => {
                console.log('Response:', response);
                alert('Appointment booked successfully!');
                // Reset the form fields
                setSelectedHospital('');
                setSelectedService('');
                setFirstName('');
                setLastName('');
                setAppointmentDate(new Date());
                setAppointmentTime(new Date());
                setEmail('');
                setServices([]);
            })
            .catch(error => {
                console.error('There was an error booking the appointment!', error.response || error);
                if (error.response && error.response.data) {
                    console.error('Response data:', error.response.data);
                }
            });
    };

    return (
        <form onSubmit={handleSubmit} className="mx-auto p-6 max-h-full w-96 bg-white shadow-md rounded-lg flex-col">
            <div className="m-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="hospital">
                    Hospital
                </label>
                <select
                    id="hospital"
                    value={selectedHospital}
                    onChange={(e) => setSelectedHospital(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                >
                    <option value="">Select a hospital</option>
                    {hospitals.map(hospital => (
                        <option key={hospital.id} value={hospital.id}>{hospital.name}</option>
                    ))}
                </select>
            </div>
            <div className="m-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="service">
                    Service
                </label>
                <select
                    id="service"
                    value={selectedService}
                    onChange={(e) => setSelectedService(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                >
                    <option value="">Select a service</option>
                    {services.map(service => {
                        console.log(service.name); // Log the service name
                        return (
                            <option key={service.id} value={service.id}>{service.name}</option>
                        );
                    })}
                </select>
            </div>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="firstName">
                    First Name
                </label>
                <input
                    id="firstName"
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                />
            </div>
            <div className="m-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="lastName">
                    Last Name
                </label>
                <input
                    id="lastName"
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                />
            </div>
            <div className="m-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="appointmentDate">
                    Appointment Date
                </label>
                <DatePicker
                    id="appointmentDate"
                    selected={appointmentDate}
                    onChange={(date) => setAppointmentDate(date)}
                    dateFormat="yyyy-MM-dd"
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                />
            </div>
            <div className="m-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="appointmentTime">
                    Appointment Time
                </label>
                <DatePicker
                    id="appointmentTime"
                    selected={appointmentTime}
                    onChange={(time) => setAppointmentTime(time)}
                    showTimeSelect
                    showTimeSelectOnly
                    timeIntervals={15}
                    timeCaption="Time"
                    dateFormat="HH:mm:ss"
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                />
            </div>
            <div className="m-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
                    Email
                </label>
                <input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded"
                    required
                />
            </div>
            <button
                type="submit"
                className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
            >
                Book Appointment
            </button>
        </form>
    );
};

export default BookingForm;
