import React, { useState } from 'react';
import "./BookStyles.css";
import Navbar from '../Components/Navbar';
import { useTranslation } from 'react-i18next';

function Book() {
  const { t } = useTranslation();
  const [selectedHospital, setSelectedHospital] = useState('');
  const [selectedService, setSelectedService] = useState('');
  const [name, setName] = useState('');
  const [lastname, setLastname] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, such as sending data to an API
    console.log('Form submitted:', { selectedHospital, selectedService, name, lastname, appointmentDate });
    // Reset form fields
    setSelectedHospital('');
    setSelectedService('');
    setName('');
    setLastname('');
    setAppointmentDate('');
  };

  const handleHospitalChange = (e) => {
    setSelectedHospital(e.target.value);
  };

  const handleServiceChange = (e) => {
    setSelectedService(e.target.value);
  };

  return (
    <>
      <Navbar />
      <div className="information">
        <h3>{t('book.importantInformation')}</h3>
        <p>{t('book.readInformation')}</p>
        <ul>
          <li>{t('book.arrivalTime')}</li>
          <li>{t('book.bringID')}</li>
          <li>{t('book.paymentRequirement')}</li>
          <li>{t('book.cancellationPolicy')}</li>
        </ul>
      </div>
      <div className='Book'>
        <div className="booking-form">
          <h1>{t('book.bookingAppointment')}</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="hospital">{t('book.hospital')}:</label>
              <select
                id="hospital"
                value={selectedHospital}
                onChange={handleHospitalChange}
                required
              >
                <option value="">{t('book.selectHospital')}</option>
                <option value="hospital1">Menelik Hospital</option>
                <option value="hospital2">Yekatit Hospital</option>
                <option value="hospital3">St.Paul's Hospital</option>
                {/* Add more options for other hospitals */}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="service">{t('book.service')}:</label>
              <select
                id="service"
                value={selectedService}
                onChange={handleServiceChange}
                required
              >
                <option value="">{t('book.selectService')}</option>
                <option value="service1">Dental</option>
                <option value="service2">Maternity</option>
                <option value="service3">Gynecology</option>
                {/* Add more options for other services */}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="name">{t('book.firstName')}:</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="lastname">{t('book.lastName')}:</label>
              <input
                type="text"
                id="lastname"
                value={lastname}
                onChange={(e) => setLastname(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="appointment-date">{t('book.appointmentDate')}:</label>
              <input
                type="date"
                id="appointment-date"
                value={appointmentDate}
                onChange={(e) => setAppointmentDate(e.target.value)}
                required
              />
            </div>
            <button type="submit">{t('book.bookAppointment')}</button>
          </form>
        </div>
        <div className='footer'>
          <div className='top'>
            <div>
              <h1>{t('footer.title')}</h1>
              <p>{t('footer.description')}</p>
            </div>
          </div>
          <div className='bottom'>
            <div>
              <h2>{t('footer.services')}</h2>
              <a href="/">{t('footer.conditions')}</a>
              <a href="/">{t('footer.listings')}</a>
              <a href="/">{t('footer.whatWeOffer')}</a>
              <a href="/">{t('footer.howItWorks')}</a>
              <a href="/">{t('footer.latestNews')}</a>
            </div>
            <div>
              <h2>{t('footer.usefulLinks')}</h2>
              <a href="http://localhost:3000/About">{t('footer.about')}</a>
              <a href="http://localhost:3000/service">{t('footer.services')}</a>
              <a href="http://localhost:3000/Hospital">{t('footer.hospitals')}</a>
              <a href="http://localhost:3000/Contact">{t('footer.contact')}</a>
            </div>
            <div>
              <h2>{t('footer.followUs')}</h2>
              <a href="/">{t('footer.facebook')}</a>
              <a href="/">{t('footer.telegram')}</a>
              <a href="/">{t('footer.whatsapp')}</a>
              <a href="/">{t('footer.twitter')}</a>
              <a href="/">{t('footer.instagram')}</a>
            </div>
            <div>
              <h2>{t('footer.contactUs')}</h2>
              <a href="/">{t('footer.email')}</a>
              <a href="/">{t('footer.phone')}</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Book;
