import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const BookingForm = () => {
    const [hospitals, setHospitals] = useState([]);
    const [services, setServices] = useState([]);
    const [selectedHospital, setSelectedHospital] = useState('');
    const [selectedService, setSelectedService] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [appointmentDate, setAppointmentDate] = useState(new Date());

    useEffect(() => {
        axios.get('http://localhost:8000/api/hospitals/')
            .then(response => {
                setHospitals(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the hospitals!', error);
            });
    }, []);

    const handleHospitalChange = (e) => {
        setSelectedHospital(e.target.value);
        axios.get(`http://localhost:8000/api/hospitals/${e.target.value}/services/`)
            .then(response => {
                setServices(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the services!', error);
            });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const booking = {
            hospital: selectedHospital,
            service: selectedService,
            first_name: firstName,
            last_name: lastName,
            appointment_date: appointmentDate.toISOString().split('T')[0],
        };
        axios.post('http://localhost:8000/api/appointments/', booking)
            .then(response => {
                alert('Appointment booked successfully!');
            })
            .catch(error => {
                console.error('There was an error booking the appointment!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Hospital:
                <select value={selectedHospital} onChange={handleHospitalChange}>
                    <option value="">Select a hospital</option>
                    {hospitals.map(hospital => (
                        <option key={hospital.id} value={hospital.id}>{hospital.name}</option>
                    ))}
                </select>
            </label>
            <br />
            <label>
                Service:
                <select value={selectedService} onChange={(e) => setSelectedService(e.target.value)}>
                    <option value="">Select a service</option>
                    {services.map(service => (
                        <option key={service.id} value={service.id}>{service.name}</option>
                    ))}
                </select>
            </label>
            <br />
            <label>
                First Name:
                <input type="text" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
            </label>
            <br />
            <label>
                Last Name:
                <input type="text" value={lastName} onChange={(e) => setLastName(e.target.value)} />
            </label>
            <br />
            <label>
                Appointment Date:
                <DatePicker selected={appointmentDate} onChange={(date) => setAppointmentDate(date)} dateFormat="yyyy-MM-dd" />
            </label>
            <br />
            <button type="submit">Book Appointment</button>
        </form>
    );
};

export default BookingForm;
